import React, { useState } from "react";
import GameCircle from "./GameCircle";
import "../Game.css"

const GameBoard = ()=>{
    const [gameBoard,setGameBoard]=useState(Array(16).fill(0))
    console.log(gameBoard)
    const clickCircle = (id)=>{
        debugger
       console.log('clicked circle ' + id)
       gameBoard[id]=1
       setGameBoard(gameBoard)
       console.log(gameBoard)
    }


    const renderCircle=id=>{
      return <GameCircle id={1} className={`player_${gameBoard[id]}`} onCircleClicked ={clickCircle}/>
    
    }
return(
   
 <div className="gameBoard">
    
   renderCircle(0) 
   renderCircle(1) 
   renderCircle(2) 
   renderCircle(3) 
   renderCircle(0) 
   renderCircle(0) 
   renderCircle(0) 
   renderCircle(0) 
   renderCircle(0) 
   renderCircle(0) 
   renderCircle(0) 

   

 </div>)
}
export default GameBoard;
