import React from 'react'
import { GAME_STATE_PLAYING ,NO_PLAYER,PLAYER_1,NO_CIRCLE, GAME_STATE_WIN} from "../Constants";




const Header=({gamestate,player,winplayer}) => {


  const renderLabel= ()=>{
    switch(gamestate){
      case GAME_STATE_PLAYING:
        return <div>Player {player} Turn </div> 
        case GAME_STATE_WIN:
       return <div> Player {player} Wins</div>
       default:
    }
  }
  return (
    <div className='panel header'>
        <div className='header-text'> Player {player} Turn</div>
      
    </div>
  )
}

export default Header
