import React, { useState } from "react";
import GameCircle from "./GameCircle";
import "../Game.css"
import Header from "./Header";
import Footer from "./Footer";
import { isWinner } from "./helper";
const NO_CIRCLE=16;

const NO_PLAYER=0;
const PLAYER_1=1;
const PLAYER_2=2;
const GAME_STATE_IDLE=0;
const GAME_STATE_PLAYING=1;
const GAME_STATE_WIN=2;
const GAME_STATE_DRAW=3;



const GameBoard = ()=>{
    const [gameBoard,setGameBoard]=useState(Array(16).fill(NO_PLAYER))
    const [currentPlayer,setcurrentPlayer]=useState(PLAYER_1)
    const [gamestate,setGameState]=useState(GAME_STATE_PLAYING)
    console.log(gameBoard)

    const initBoard=()=>{
        const circles=[]
        for(let i=0;i<NO_CIRCLE;i++){
            circles.push(renderCircle(i))
        }
        return circles
    }
    const clickCircle = (id)=>{
        
       console.log('clicked circle ' + id)
       if(isWinner(gameBoard, id,currentPlayer)){
   setGameState(GAME_STATE_WIN)
    }
       setGameBoard(prev =>{
       return prev.map((circles,pos)=>{
        if(pos===id)return currentPlayer;
        return circles
       })
    })
       
       setcurrentPlayer(currentPlayer===PLAYER_1?PLAYER_2:PLAYER_1)
    }


    const renderCircle=id=>{
      return <GameCircle id={id} key={id} className={`player_${gameBoard[id]}`} onCircleClicked ={clickCircle}/>
    
    }
return(
    <>
   <Header gamestate={gamestate} player={currentPlayer}/>
 <div className="gameBoard">
 {initBoard()}

 </div>
 <Footer/>
 </>
 )
}
export default GameBoard;
